from selection_sort import selection_sort

def test_selection_sort_1():
    assert selection_sort([3, 4, 6, 5, 10, 1]) == [1, 3, 4, 5, 6, 10]

def test_selection_sort_2():
    assert selection_sort([12, -4, 7, 0, 5, 15, -3, 9, 2, -8]) == [-8, -4, -3, 0, 2, 5, 7, 9, 12, 15]

